import React, {Component} from 'react'
import '../css/About.css';
import Header from './Header';
import Slider from './Slider';
//import API from './API'


//import {NavLink} from "react-router-dom";


export default class About extends Component {
    constructor(props){
        super(props);
        this.input = React.createRef();
        this.handleSubmit = this.handleSubmit.bind(this);
       this.state = {
            list: [],
            city: ''
          };
    }



   componentDidMount (){
   
    fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&limit=200&offset=0&city=`  + this.city.value, {
        "method": "GET",
        "headers": {
            "x-rapidapi-host": "realtor.p.rapidapi.com",
            "x-rapidapi-key": "f9871e9028mshc949b36dd6ede42p19eea8jsnfe9903109b31"
        }
    })
    .then(res => res.json())
    .then(
      (list) => {
        this.setState({
          isLoaded: true,
          list: list,
          city: city.value
        });
      },
      // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
      // чтобы не перехватывать исключения из ошибок в самих компонентах.
      (city) => {
        this.setState({
          isLoaded: true,
          city: city
        });
      }
    )
    .catch(err => {
        console.log(err);
    });
   }
  

   /*async findHome(){
      fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&limit=20&offset=0&city=${this.state.city.value}`, {
                "method": "GET",
                "headers": {
                    "x-rapidapi-host": "realtor.p.rapidapi.com",
                    "x-rapidapi-key": "f9871e9028mshc949b36dd6ede42p19eea8jsnfe9903109b31"
                }
               
            }) 
           
       
            .then(function(response){
               
                console.log(response)
                
                return response.json();
                
    
            })
            .then(function(data) {
                console.log(data)
                /*data.properties.forEach(function(item){
                    let pElem = document.createElement('li');
                    
                    let elemItems = `
                    <a href= " ${item.rdc_web_url}" target="_blank">${item.address.city}${item.address.line}${item.address.postal_code}${item.address.state}</a>.,${item.baths},${item.beds},$${item.price}, ${item.building_size.size}, ${item.office.name}<br/>
                   \n <img src=${item.thumbnail} />, `;
                    console.log(item.Media)
                    pElem.innerHTML = elemItems;
                    let recipe_result = document.querySelector('.recipe_response ul');
    
                    recipe_result.appendChild(pElem);
                    let element = document.getElementById('body');
                    element.appendChild(recipe_result);
                });*/
        //})
        
    //}
  
    

    onSubmitHandler = (event) =>{
        event.preventDefault()
        const data = this.state;
        console.log(data)

      
        //if(!this.state.city.value) return;
       
    }


    /*onChangeHandler = (event) => {
        event.preventDefault()
        const value = event.target.name.value

         this.setState({
            city: value
        })
      
        console.log(event)
        console.log(event.target.name)
        console.log(event.target.value)
    }*/

    handleSubmit(event) {
        alert('Отправленное имя: ' + this.input.current.value);
        event.preventDefault();
      }
    

    render() {
       
        return(
            
        <div className="section-about" id="/">
                <Header />
                <Slider />
                <div className="container-main">
                    <div className='about-wrapper'>
                    <div className="about-image">
                        <img src='images/1(3).jpg' alt=''></img>
                    </div>
                    <div className="about-text">
                        <h2>About Katerina</h2>
                        <div className='about-text-paragraph'>
                        <div>
                            Dedicated real estate specialist. Successfully transfers her previous expereince in the homeimprovement
                        business to highly personal approach to every client.
                        </div><br/>
                    
                        <div>
                            Known for her integrity, knowledge, commitment, honesty,
                        she understands how to handle both emotional and logical sides of the transaction.</div><br/>

                        <div className='about-text-bold'>” I understand how stressfull could be the process of buying or selling your residence, so I am always there to support
                        and to educate ” – Katerina.
                        </div> <br/>

                        <div>Katerina is also a great resource of contractors, handymen, cleaning crews and moving companies, and creates long-lasting
                        relationship with each client.
                        </div> <br/>

                        </div>
                        </div>
                    </div>
                <section className="testimonials" id="testimonials">
                        <h2 className='testimonials-h2'>Testimonials</h2>
                        <div className="testimonials_list">
                            <div className="testimonials_item">
                                <div className='testimonial_thumbnail1'></div>
                                <h3>Maria E. Flynn</h3>
                                <p className='testimonials_description'>MANAGER AT COLDHAWK INC.</p>
                                <p>Katerina does an incredible amount of research, she comes prepared which makes your decision making so much easier. She supports you throughout the entire process an asset for first time buyers!</p>
                            </div>
                            <div className="testimonials_item">
                                <div className='testimonial_thumbnail2'></div>
                                <h3>Dalmar Johnson</h3>
                                <p className='testimonials_description'>FOUNDER OF WEBFLEX INC.</p>
                                <p>Thanks so much for your patience and expertise with helping my family, find a home! You have been very professional and honest, when it’s come to listing our home as well. Thank you!</p>
                            </div>
                            <div className="testimonials_item">
                                <div className='testimonial_thumbnail3'></div>
                                <h3>David S. Morris</h3>
                                <p className='testimonials_description'>CEO AT ENTAVO LLC</p>
                                <p>Great service!! Bought and sold for us with great deals on both sides. He had as much time for us as we needed and gave great advice. She’s very knowledgeable. Highly recommended!</p>
                            </div>
                        </div>
                </section>
                <section>
                  
                    <div className="recipePuppy">
                        <div className="search">
                            <h1>Find your Recipe!</h1>
                            <form onSubmit={this.handleSubmit}>
                                <span className="inputSearch" method="get" > Search by an Ingredient:</span> : 
                                <div>
                                    <input type="text" name="city" placeholder="Search" ref={this.input} />
                                   
                            <button onClick={this.onSubmitHandler} className="searchBut" type="submit" value= "search">Search</button>
                        </div>
                        </form>
                      { /*<form className="inputSearch" method="get" onSubmit={this.onSubmitHandler}>
                 <div className="inputTitle">
                    Search by an Ingredient:
                </div>
                <input type="text" name="city" placeholder="Search"  onChange={this.onChangeHandler}/>
                <button onClick={this.addWordToLibrary} className="searchBut" type="submit" value= "search">Search</button>
                </form>*/}

        </div>
            
            </div>
                </section>
            </div>
           
        </div>
        )
        }
    }
