import React from 'react';
import '../css/API.css';


 class API extends React.Component{
    state={
        city: undefined,
        line: undefined,
        neighborhood_name: undefined,
        rdc_web_url: undefined,
        postal_code:undefined,
        stateUS:undefined,
        baths:undefined,
        price:undefined,
        beds:undefined,
        building_size_size:undefined,
        thumbnail: undefined,
        virtual_tour: undefined,
        error: undefined
      }
    
    sellHouse = async(e) => {
        e.preventDefault();
        const city = e.target.elements.city.value;
        const beds = e.target.elements.state_code.value;
        const api_url = await fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&limit=30&city=${city}&state_code=${beds}`, {
          "method": "GET",
          "headers": {
            "x-rapidapi-host": "realtor.p.rapidapi.com",
            "x-rapidapi-key": "2f3474695cmsh1bdef148d0dfa1cp1cbb64jsne8cc57bfbe07"
        }
      })
      const properties = await api_url.json();
      const data= properties;
      console.log(data)
      
      if(city) {
      this.setState({
        city: data.city,
        line: properties.line,
        rdc_web_url: properties.rdc_web_url,
        postal_code:properties.postal_code,
        stateUS:properties.state,
        baths:properties.baths,
        price:properties.price,
        beds:properties.beds,
        neighborhood_name:properties.neighborhood_name,
        thumbnail: properties.thumbnail,
        virtual_tour: properties.virtual_tour,
        error: ""
      });
    
    }
}

    

     render() {
   
         return(
             <div className='form-api' id='find-a-house'>
                 <div className='container-main'>
                    <form onSubmit={this.props.listSale}>
                        <h2 className='formHomeApi'>Find your dream House!<i className="fa fa-key" aria-hidden="true"></i></h2>
                        <div className='inputs'>
                            <input className='input-api' type="text" name="city" placeholder="Where do you want to live?" />
                            <input className='input-api-state' type="text" name="state_code" placeholder="State" />
                        </div>
                        <button className='btn-api' >Get a House</button>
                    </form>
                     <div className='houses-container'>
                     {this.data.properties.map((item, index) => (
                        <div key={index}
                        > 
                            <ul>
                                <li>
                                {item.rdc_web_url}
                                {item.address.city}  
                                {item.address.line}  
                                {item.address.postal_code}
                                </li>
                            </ul>
                            <div onClick={() => this.removeWordFromLibrary(index)}>Delete</div>
                        </div>
                    ))}
                    </div> 
               </div>        
            </div>      
         )
    }
    
    
 }

export default API
