import React, {Component} from 'react'
import '../css/ContactUs.css';
import MenuHam from './Nav/MenuHam';

export default class ContactUs extends Component {

    state = {
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        subject: '',
        message: '',
        firstNameError: '',
        lastNameError: '',
        emailError: '',
        phoneError: '',
        messageError: ''
    };
    

    validation = () => {
        //let firstNameError = '';
        //let lastNameError = '';
        let emailError = '';
        //let phoneError = '';
        //let messageError = '';

        if(!this.state.email.includes('@')){
            emailError = 'invalid email';
        }

        if(emailError){
            this.setState({emailError});
            return false;
        }

        return true;
    };

    onSubmit = (event) => {
        event.preventDefault()
        const isValid = this.validation;
        if (isValid){
            console.log(this.state);
        }
    }

    render() {
        return(
            <div className='contact' id='contact-us'>
                <MenuHam />
               <div className='container-main'>
                    <h2>Get In Touch</h2>
                    <p className='contact-us-paragraph'>We would love to hear from you!</p>
                    <p className='contact-us-paragraph'> Send us a message and we’ll get right back in touch.</p>
                    <form method="post" onSubmit={event => this.onSubmit(event)} > 
                        <div className='contact-fields input-contact'>
                            <div className='firstLine-contact'>
                                <input style={{position: "relative"}} className='input-contact' value={this.state.firstName} onChange={e => this.setState({firstName: e.target.value}) } type='text' placeholder='First Name'/>
                                <div style={{position: "absolute", bottom: '0', color: 'red'}}>{this.state.firstNameError}</div>
                            </div>
                            <div className='firstLine-contact'>
                                <input  style={{position: "relative"}} className='input-contact' value={this.state.lastName} onChange={e => this.setState({lastName: e.target.value}) } type='text' placeholder='Last Name'/>
                                <div style={{position: "absolute", bottom: '0', color: 'red'}}>{this.state.lastNameError}</div>
                            </div>
                            <div className='firstLine-contact'>
                                <input style={{position: "relative"}} className='input-contact' value={this.state.phone} onChange={e => this.setState({phone: e.target.value}) } type='phone' placeholder='Phone'/>
                                <div style={{position: "absolute", bottom: '0', color: 'red'}}>{this.state.phoneError}</div>
                            </div>
                        </div>
                        <div className='message-field input-contact'>
                            <div>
                                <input className='input-contact' value={this.state.email} onChange={e => this.setState({email: e.target.value}) } type='email' placeholder='E-mail'/>
                                <div>{this.state.firstNameError}</div>
                            </div>
                            <div>
                                <input className='input-contact' value={this.state.subject} onChange={e => this.setState({subject: e.target.value}) } type='text' placeholder='Subject' required/>
                                <div>{this.state.firstNameError}</div>
                            </div>
                            <div>
                                <textarea className='input-contact' value={this.state.message} onChange={e => this.setState({message: e.target.value}) } placeholder="Message" name="message" maxLength="500"></textarea>
                                <div>{this.state.firstNameError}</div>
                            </div>
                        </div>
                    </form>
                    <div className='btn-form'>
                        <button className="button_message"  type="submit">Submit</button>
                    </div>
                </div>
            </div>
        )
    } 
}

