import React from 'react';
import { render } from 'react-dom';


const Api = () => {

    let form = document.querySelector('.inputSearch'), 
        recipe_result = document.querySelector('.recipe_response ul');

    if(!form || !recipe_result) return;

    let button = form.querySelector('.searchBut'),
        i = form.querySelector('[name="i"]');

    if(!button || !i) return;

    const lstingResult = function(results){
        if (!results) return;
    };


    const searchRecipe = async function (){
        fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&limit=20&offset=0&city=` + i.value, {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "realtor.p.rapidapi.com",
                "x-rapidapi-key": "f9871e9028mshc949b36dd6ede42p19eea8jsnfe9903109b31"
            }
        })
        .then(function(response){
            console.log(response)


            return response.json();
            

        })
        .then(function(data) {
            lstingResult(data.properties);
            console.log(data)

            data.properties.forEach(function(item){
                let pElem = document.createElement('li');
                
                let elemItems = `
                <a href= " ${item.rdc_web_url}" target="_blank">${item.address.city}${item.address.line}${item.address.postal_code}${item.address.state}</a>.,${item.baths},${item.beds},$${item.price}, ${item.building_size.size}, ${item.office.name}<br/>
               \n <img src=${item.thumbnail} />, `;
                console.log(item.Media)
                pElem.innerHTML = elemItems;

                recipe_result.appendChild(pElem);
                let element = document.getElementById('body');
                element.appendChild(recipe_result);
            });
        })
        
    
    }
    

    button.addEventListener('click', function(event) {
        event.preventDefault();

        if(!i.value) return;
        searchRecipe(i.value);
    });

    button.addEventListener('mouseover', function() {
        this.style.backgroundColor = '#8c8f71';
    });

    button.addEventListener('mouseout', function() {
        this.style.backgroundColor = '#d9d9d9';
    });
    render =() =>{
    return(
        <div class="recipePuppy">
        <div class="search">
            <h1>Find your Recipe!</h1>
            <form class="inputSearch" method="get">
                 <div class="inputTitle">
                    Search by an Ingredient:
                </div>
                 <input type="text" name="i" id="addIng" size='50'  value="" />
                 
                <button class="searchBut" type="submit" value= "search">Search</button>
            </form>
        </div>
        <div class="recipe_response">
            <p id="items"></p>
            <ul></ul>
        </div>
    </div>
    )
    }
    
};

export default Api;
