import React, { Component, useState } from 'react';
import '../../css/Property.css';
import ImgSlider from '../ImgSlider';
import house1_1 from '../../images/house1_1.jpg';
import house1_2 from '../../images/house1_1.jpg';
import house1_3 from '../../images/house1_1.jpg';
import house1_4 from '../../images/house1_1.jpg';
import house2 from '../../images/house1_1.jpg';

//import images from '../../images';
//import {NavLink} from "react-router-dom";


function PopUp () {

    let sliderArrImg = [<ImgSlider src={house1_1}/>, <ImgSlider src={house1_2}/>,<ImgSlider src={house1_3}/>,<ImgSlider src={house1_4}/>,<ImgSlider src={house2}/>];
    const [slide, setSlide] = useState(0);

    const prev = () => {
        slide === 0 ? setSlide(-100* (sliderArrImg.length - 1)) : setSlide(slide+100);
    };
    const next = () => {
        slide === -100* (sliderArrImg.length - 1) ? setSlide(0) : setSlide(slide-100);
    };
    
        return (
            <div className='popupParent'>
                <div className='popupImage'>
                {sliderArrImg.map((item, index) => {
                   return(
                       <div key={index} className='slide' style={{transform: `translateX(${slide}%)`}}>
                           {item}
                       </div>
                   )
               })
           }
                    <button className='imgClose-btn' onClick={this.props.closePopup}>X</button>
                    <img src={this.props.popupImage} alt=''/>
                </div>
            </div>
        )
    }


export default PopUp;




