import React from 'react';

class API extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            isOpen: true,
            translation: '',
            value: ''
        }
        this.getValue = this.getValue.bind(this)
    }

    async findHome() {
        try {
            const response = await fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&limit=200&offset=0&city=${this.state.value}`, {
                "method": "GET",
                "headers": {
                    "x-rapidapi-host": "realtor.p.rapidapi.com",
                    "x-rapidapi-key": "f9871e9028mshc949b36dd6ede42p19eea8jsnfe9903109b31"
        }
            })
            const result = await response.json()

            if (result.properties) {
                await this.setState(()=> ({
                    translation: result.properties
                }))
            }

            await this.setState(()=> ({
                translation: ''
            }))
        }
        
            catch (error) {
                console.log(error)
            }
    }

    async getValue(event){
        const value = event.currentTarget.value
        this.setState(()=> ({
            value: value
        }))
           
    }


    render() {
        return(
            <div className="page-container">
                <div className="add-word-container">
                    {this.state.isOpen ? 
                        <span className="label-title">Add new word</span> : 
                        <div>
                            <input onChange={this.getValue} placeholder = "Enter new word"/>
                            <span>{this.state.translation}</span>
                            <button onClick={this.findHome} className = "btn-check"> âœ“</button>
                        </div>
                    }
                </div>
                    {this.state.library.map((word, index) => (
                        <div key={index}
                        ref={el => this.wordsRef[index] = el}
                        > 
                            <div>
                                {word.id}
                            </div>
                            <div>
                                {word.word}
                            </div>
                            <div>
                                {word.translation}
                            </div>
                            <div onClick={() => this.removeWordFromLibrary(index)}>Delete</div>
                        </div>
                    ))}
                   
                
                {/*<button onClick={this.checkWord}>Check the word</button>*/}
            </div>
        )
    }

}

export default API
