const Slider = () =>  {
    const [slide, setSlide] = useState({
        images: ['1.jpg', '2.jpg', '3.jpg', '4.jpg', '5.jpg', '6.jpg'],
        imageIndex: 0
    })

    const shiftSlide = function () {
        setTimeout(() => {
            this.setState({
                imageIndex: this.state.imageIndex < this.state.images.length - 1 ? this.state.imageIndex + 1 : 0
            })
            this.shiftSlide()
        }, 2000)
    }

    const prev = function(){
        if(slide.imageIndex <= 0) slide.imageIndex = slide.images.length;
        slide.imageIndex --;
        return changeSlide();
    }
    
    const next  = function(){
        if (slide.imageIndex >=slide.images.length - 1) slide.imageIndex = -1;
        slide.imageIndex++;
        return changeSlide();
    }

    const changeSlide = function() {
        setTimeout(() => {
            this.setState({
                imageIndex: this.state.imageIndex < this.state.images.length - 1 ? this.state.imageIndex + 1 : 0
            })
            this.changeSlide()
        }, 5000)
        const slider_img = document.querySelector('.img_slide')
        return slider_img.setAttribute('src', 'images/' + this.state.images[this.state.imageIndex]);
        
    }

