import React, { useState, useEffect } from 'react';
import {BrowserRouter, Route, Link, useHistory, NavLink} from 'react-router-dom';
import './App.css';
import Header from './Components/Header'
//import Nav from './Components/Nav';
import Slider from './Components/Slider'
import About from './Components/About'
import News from './Components/News'
import Property from './Components/Property'
import ContactUs from './Components/ContactUs'

function App() {

    const history = useHistory('');
    const [path, setPath] = useState('');

    window.addEventListener('load', () =>{
      if(window.location.pathname === '/about' 
        && window.location.pathname === '/news'
        && window.location.pathname === '/property'
        && window.location.pathname === '/contact-us')
      setPath(window.location.pathname)
    })

    const checkPath = () => {
      history.listen((location) =>{
        setPath(location.name)
      });
    }

    useEffect(() => {
      checkPath();
    }, []);

    const showRoute = path;
    let about;
    if(showRoute != './about'){
      about = (<li><Link to='./about'>About</Link></li>)
    }

  return (
    <BrowserRouter>
    <div className='realtor-page'>
      <Header />
      <Slider />
      <NavLink to='/about'>About Katerina</NavLink>
                <NavLink to='/news'>News</NavLink>
                <NavLink to='/property'>Featured Property</NavLink>
    <NavLink to='/contact-us'>Contact Us</NavLink>
    </div>


    </BrowserRouter>
  );
}

export default App;
