import React from 'react';

 class API extends React.Component{
    sellHouse = async(e) => {
        e.preventDefault();
        const city = e.target.elements.city.value;
        const api_url = await fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&limit=50&city=${city}`, {
          "method": "GET",
          "headers": {
              "x-rapidapi-host": "realtor.p.rapidapi.com",
              "x-rapidapi-key": "f9871e9028mshc949b36dd6ede42p19eea8jsnfe9903109b31"
          }
      })
      const properties = await api_url.json();
      const data= properties;
      console.log(data);
      
      this.setState({
        city: data.city,
        line: properties.line,
        rdc_web_url: properties.rdc_web_url,
        postal_code:properties.postal_code,
        stateUS:properties.state,
        baths:properties.baths,
        price:properties.price,
        beds:properties.beds,
        neighborhood_name:properties.neighborhood_name,
        thumbnail: properties.thumbnail,
        virtual_tour: properties.virtual_tour,
        error: ""
      });
      data.properties.forEach(function(item){
          let pElem = document.createElement('li');
          let recipe_result = document.createElement('ul')
          let elemItems = `
          <a href= " ${item.rdc_web_url}" target="_blank">${item.address.city}${item.address.line}${item.address.postal_code}${item.address.state}</a>.,${item.baths},${item.beds},$${item.price}, ${item.building_size.size}, ${item.office.name}<br/>
         \n <img src=${item.thumbnail} />, `;
          console.log(item.Media)
          pElem.innerHTML = elemItems;
      
          recipe_result.appendChild(pElem);
          let element = document.getElementById('body');
          element.appendChild(recipe_result);
      });
    }

     render(){
       
        
      
      
       
         return(
             <div>
                <form onSubmit={this.props.listSale}>
                    <input type="text" name="city" placeholder="city" />
                    <button >Get a House</button>
                </form>
                
            </div>
                    
             
         )
     }
 }

export default API;
