import React from 'react';

 class API extends React.Component{
    state={
        city: undefined,
        line: undefined,
        neighborhood_name: undefined,
        rdc_web_url: undefined,
        postal_code:undefined,
        stateUS:undefined,
        baths:undefined,
        price:undefined,
        beds:undefined,
        building_size_size:undefined,
        thumbnail: undefined,
        virtual_tour: undefined,
        error: undefined
      }
       
        sellHouse = async(e) => {
          e.preventDefault();
          const city = e.target.elements.city.value;
          const api_url = await fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&limit=50&city=${city}`, {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "realtor.p.rapidapi.com",
                "x-rapidapi-key": "f9871e9028mshc949b36dd6ede42p19eea8jsnfe9903109b31"
            }
        })
        const data = await api_url.json();
        console.log(data.properties);
    
        this.setState({
          city: data.city,
          line: data.line,
          rdc_web_url: data.rdc_web_url,
          postal_code:data.postal_code,
          stateUS:data.state,
          baths:data.baths,
          price:data.price,
          beds:data.beds,
          neighborhood_name:data.neighborhood_name,
          thumbnail: data.thumbnail,
          virtual_tour: data.virtual_tour,
          error: ""
        });
      }
     
     render(){
        let pElem = document.createElement('li');
                
        let elemItems = `
        <a href= " ${item.rdc_web_url}" target="_blank">${item.address.city}${item.address.line}${item.address.postal_code}${item.address.state}</a>.,${item.baths},${item.beds},$${item.price}, ${item.building_size.size}, ${item.office.name}<br/>
       \n <img src=${item.thumbnail} />, `;
        console.log(item.Media)
        pElem.innerHTML = elemItems;
        
         return(
             <div>
                <form onSubmit={this.props.listSale}>
                    <input type="text" name="city" placeholder="city" />
                    <button >Get a House</button>
                </form>
                    
                    <div className='User-info--data'>
                            <p className='User-info--name'>{item.baths}</p>
                            <p className='User-info--email'>{item.beds}</p>
                    </div>
                    
             </div>
         )
     }
 }

export default API;
