import React from 'react'

class API extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            list: [],
            city: null
          };
    }

   componentDidMount (){
   
    fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&city=${this.state.city}&limit=200&offset=0&state_code=NY`, {
        "method": "GET",
        "headers": {
            "x-rapidapi-host": "realtor.p.rapidapi.com",
            "x-rapidapi-key": "f9871e9028mshc949b36dd6ede42p19eea8jsnfe9903109b31"
        }
    })
    .then(res => res.json())
    
    .then(list => {
        this.setState({list});
        
    })
    
    .catch(err => {
        console.log(err);
    });
   }
    

    onSubmitHandler = (event) =>{
        event.preventDefault()
        const data = this.state;
        console.log(data)
    }


    onChangeHandler = (event) => {
        event.preventDefault()
        const value = event.target.name.value

         this.setState({
            city: value
        })
      
        console.log(event)
        console.log(event.target.name)
        console.log(event.target.value)
    }
    

    render() {
       
        return(
            <div className="section-about" id="/">
                <div className="recipePuppy">
                    <div className="search">
                        <h1>Find your Recipe!</h1>
                            <span className="inputSearch" method="get" > Search by an Ingredient:</span> : 
                                <div>
                                    <input type="text" name="city" placeholder="Search"  onChange={this.onChangeHandler}/>    
                                    <button onClick={this.onSubmitHandler} className="searchBut" type="submit" value= "search">Search</button>
                                </div>
                    
                    </div>
                
                </div>
                    
            </div>
            
            
            )
        }
    }

    export default API;
