import React, {Fragment} from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import './App.css';
//import Header from './Components/Header'
//import Nav from './Components/Nav';
//import Slider from './Components/Slider';
import About from './Components/About'
import Services from './Components/Services/Services'
import Buyers from './Components/Services/Buyers'
//import Sellers from './Components/Services/Sellers'
import Property from './Components/Property/Property'
import ContactUs from './Components/ContactUs'
import Footer from './Components/Footer';
import API from './Components/API'



class App extends React.Component{
  state={
    city: undefined,
    line: undefined,
    neighborhood_name: undefined,
    rdc_web_url: undefined,
    postal_code:undefined,
    stateUS:undefined,
    baths:undefined,
    price:undefined,
    beds:undefined,
    building_size_size:undefined,
    thumbnail: undefined,
    virtual_tour: undefined,
    error: undefined
  }

sellHouse = async(e) => {
    e.preventDefault();
    const city = e.target.elements.city.value;
    const beds = e.target.element.beds.value;
    const api_url = await fetch(`https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&limit=30&city=${city}&beds=${beds}`, {
      "method": "GET",
      "headers": {
        "x-rapidapi-host": "realtor.p.rapidapi.com",
        "x-rapidapi-key": "2f3474695cmsh1bdef148d0dfa1cp1cbb64jsne8cc57bfbe07"
	}
  })
  const properties = await api_url.json();
  const data= properties;

  if(city) {
  this.setState({
    city: data.city,
    line: properties.line,
    rdc_web_url: properties.rdc_web_url,
    postal_code:properties.postal_code,
    stateUS:properties.state,
    baths:properties.baths,
    price:properties.price,
    beds:properties.beds,
    neighborhood_name:properties.neighborhood_name,
    thumbnail: properties.thumbnail,
    virtual_tour: properties.virtual_tour,
    error: ""
  });
  }
  data.properties.forEach(function(item) {
      let recipe_result = document.createElement('ul')
      let pElem = document.createElement('li');
      pElem.classList.add('list_name');

      let elemItems = `
      <a href= " ${item.rdc_web_url}" target="_blank">${item.address.city}  ${item.address.line}  ${item.address.postal_code}, ${item.address.state}</a><br/> Baths: ${item.baths}, beds: ${item.beds}, ${item.office.name}<br/>$${item.price}<br/>
     \n <img class='img-api' src=${item.thumbnail} /> `;
      pElem.innerHTML = elemItems;


     recipe_result.appendChild(pElem);
      let element = document.querySelector('.houses-container');
      element.appendChild( recipe_result);
  });

}

    render() {

      return (

      <Router>
        <Fragment>
          <Route exact path='/'>
                  <About 
                     city={this.state.city}
                     line={this.state.line}
                     rdc_web_url={this.state.rdc_web_url}
                     postal_code={this.state.postal_code}
                     stateUS={this.state.stateUS}
                     baths={this.state.baths}
                     price={this.state.price}
                     beds={this.state.beds}
                     neighborhood_name={this.state.neighborhood_name}
                     thumbnail={this.state.thumbnail}
                     virtual_tour={this.state.virtual_tour}
                    error={this.state.error}
                  />
                  <API  listSale={this.sellHouse}

                  />


            </Route>

            <Route path='/property' component={Property} />
            <Route path='/services' component={Services} />
            <Route path='/contact-us' component={ContactUs} />
            <Route path='/buy-sell' component={Buyers} />
          <Footer />
        </Fragment>
      </Router>
      )
    }
}

export default App;
