import React, { Component } from 'react';


class Slider extends Component {
    constructor(props){
        super(props);
        this.state = {
            slider_img: document.querySelector('.img_slide'),
            images: ['1.jpg', '2.jpg', '3.jpg', '4.jpg', '5.jpg', '6.jpg'],
            i: 0
        }
        this.prev = this.prev.bind(this)
        this.next = this.next.bind(this)
    }
    

    prev (){
        if(this.state.i <= 0) this.state.i  = this.state.images.length;
        this.state.i --;
        return this.changeSlide();
    }

    next () {
        if (this.state.i >=this.state.images.length - 1) this.state.i = -1;
        this.state.i++;
        return this.changeSlide();
    }

    changeSlide () {
        const slider_img = document.querySelector('.img_slide')
        return slider_img.setAttribute('src', 'images/' + this.state.images[this.state.i]);
    }

    render(){
        
        return(
            
        <div className="img-slider">
        <img src="images/1.jpg" alt="" className="img_slide"/>
            <button onClick={this.prev} className="slider-navigation prev">Prev</button>
            <button onClick={this.next} className="slider-navigation next">Next</button>
        </div>
        )
    }
}


export default Slider;
