import React from 'react';
import '../css/API.css';


 class API extends React.Component{
    clear () {
        document.querySelector('.houses-container ul').innerHTML = '';
    };

     render(){
   
         return(
             <div className='form-api'>
                 <div className='container-main'>
                    <form onSubmit={this.props.listSale}>
                        <h2 className='formHomeApi'>Find your dream House!<i className="fa fa-key" aria-hidden="true"></i></h2>
                        <input className='input-api' type="text" name="city" placeholder="Where do you want to live?" />
                        <button className='btn-api' >Get a House</button>
                        <button onClick={this.clear} className='btn-api-clear' >Choose another city</button>
                    </form>
                     <div className='houses-container'></div> 
               </div>        
            </div>      
         )
     }
 }

export default API;
